
using Microsoft.AspNetCore.Mvc;
using RESTcursos.Entities;

namespace RESTcursos.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class CursoController : ControllerBase
    {

        // GET: api/Curso
        [Authorize]
        [HttpGet("ultimos")]
        public IActionResult  GetUltimos()
        {
            return Ok(new Curso[] { 
                new Curso { id = 1, titulo = "Git", descripcion = "Curso de Git Básico que nos permitirá trabajar con repositorios locales y remotos", url = "gitbasico", thumbnail = ""},
                new Curso { id = 2, titulo = "Bootstrap", descripcion = "Curso de Bootstrap para la maquetación de páginas web", url = "bootstrap", thumbnail=""},
                new Curso { id = 3, titulo = "GitHub", descripcion = "Curso de GitHub. Trabajaremos con repositorios remotos y flujos de trabajo.", url = "github", thumbnail=""},
                new Curso { id = 4, titulo = "Jasmine", descripcion = "Curso de Jasmine para test unitarios.", url = "jasine", thumbnail=""},
                new Curso { id = 5, titulo = "Azure DevOps", descripcion = "Completo curso de Azure DevOps donde veremos repositorios, scrum y CI /CD.", url = "azuredevops", thumbnail= ""}
            });
        }

        // GET: api/Curso
        [Authorize]
        [HttpGet("valorados")]
        public IActionResult  GetValorados()
        {
            return Ok(new Curso[] {
                new Curso { id = 1, titulo = "Git", descripcion = "Curso de Git Básico que nos permitirá trabajar con repositorios locales y remotos", url = "gitbasico", thumbnail = ""},
                new Curso { id = 2, titulo = "Azure DevOps", descripcion = "Completo curso de Azure DevOps donde veremos repositorios, scrum y CI /CD.", url = "azuredevops", thumbnail= ""},
                new Curso { id = 3, titulo = "Java", descripcion = "Curso de introducción a java", url = "java", thumbnail = ""},
                new Curso { id = 4, titulo = "HTML, CSS y JavaScript", descripcion = "En este curso veremos HTML5, CSS3 y JavaScript", url = "html", thumbnail=""},
                new Curso { id = 5, titulo = "Jasmine", descripcion = "Curso de Jasmine para test unitarios.", url = "jasine", thumbnail=""}
            });
        }

        // GET: api/Curso/5
        [Authorize]
        [HttpGet("categoria/{id}", Name = "Get")]
        public IActionResult  GetCursosCategoria(int id)
        {
            switch (id)
            {
                case 1:
                    return Ok(new Curso[] {
                        new Curso { id = 1, titulo = "Git Básico", descripcion = "Curso de Git Básico", url = "gitbasico", thumbnail = ""},
                        new Curso { id = 2, titulo = "Git Avanzado", descripcion = "Curso de Git Avanzado", url = "gitavanzado", thumbnail = ""},
                        new Curso { id = 3, titulo = "Java Básico", descripcion = "Curso de Java Básico", url = "javabasico", thumbnail = ""},
                        new Curso { id = 4, titulo = "Java Medio", descripcion = "Curso de Java Medio", url = "javamedio", thumbnail = ""},
                        new Curso { id = 5, titulo = "Java Avanzado", descripcion = "Curso de Java Avanzado", url = "javaavanzado", thumbnail = ""}
                    });
                case 2:
                    return Ok(new Curso[] {
                        new Curso { id = 6, titulo = "Cálculo I", descripcion = "Curso de Cálculo I", url = "calculo1", thumbnail="data:image/jpeg;base64,"},
                        new Curso { id = 7, titulo = "Cálculo II", descripcion = "Curso de Cálcilo II", url = "calculo2", thumbnail="data:image/jpeg;base64,"},
                    });
                case 3:
                    return Ok(new Curso[] {
                        new Curso { id = 8, titulo = "Biología", descripcion = "Curso de Biología", url = "biologia", thumbnail="data:image/jpeg;base64,"}
                    });
                default:
                    return Ok(new Curso[] { });
            }
        }

